  create policy "Enable crud for authenticated users only"
  on "public"."dishes_allergens"
  as permissive
  for all
  to authenticated
using (true);



  create policy "CRUD for auth users"
  on "public"."hospital_services"
  as permissive
  for all
  to authenticated
using (true);



  create policy "Enable read access for all users"
  on "public"."hospital_services"
  as permissive
  for select
  to public
using (true);



  create policy "Allow delete for kitchen staff"
  on "public"."menu_planning"
  as permissive
  for delete
  to authenticated
using ((EXISTS ( SELECT 1
   FROM public.profiles
  WHERE ((profiles.id = auth.uid()) AND (profiles.role = 'KITCHEN'::public.profile_roles)))));



  create policy "Allow insert for kitchen staff"
  on "public"."menu_planning"
  as permissive
  for insert
  to authenticated
with check ((EXISTS ( SELECT 1
   FROM public.profiles
  WHERE ((profiles.id = auth.uid()) AND (profiles.role = 'KITCHEN'::public.profile_roles)))));



  create policy "Allow read access for authenticated users"
  on "public"."menu_planning"
  as permissive
